const modalChoices=document.querySelectorAll(".modal__choice");function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(c.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))})),c.addEventListener("click",(s=>{s.target&&(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function bindModal(e,c,t){e=document.querySelector(e),c=document.querySelector(c),t=document.querySelector(t);const s=document.body;e.addEventListener("click",(e=>{e.preventDefault(),c.style.display="flex",s.classList.add("locked")})),t.addEventListener("click",(()=>{c.style.display="none",s.classList.remove("locked")})),c.addEventListener("click",(e=>{e.target===c&&(c.style.display="none",s.classList.remove("locked"))}))}modalChoices.forEach((e=>{const c=e.querySelectorAll(".modal__choice-link");c.forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault(),c.forEach((e=>e.classList.remove("active"))),this.classList.add("active")}))}))})),burgerMenu(),window.addEventListener("scroll",fixedNav),bindModal(".modal__btn",".modal__wrapper",".modal__close");